var yylibui = require('../../yylib2/index');
var YYSearchCondition = require('../search/YYSearchCondition');
var YYSearchItem = require('../search/YYSearchItem');
var YYStepsNav = require('../stepsnav/YYStepsNav');
var YYStepsNavItem = require('../stepsnav/YYStepsItem');
var YYEditor = require('yylib-ui/editor/YYEditor');
var widgets = require('./widgets/index');
var YYEnumSelect = require('../enum/YYEnumSelect');
//建立模板uitype对应的组件解析器
var DefaultParser = {};

//初始化yylib-ui提供的组件解析器
for (var key in yylibui) {
    var comp = yylibui[key];
    DefaultParser[key] = comp;
}
//初始化widgets的组件解析器
for (var key in widgets) {
    var comp = widgets[key];
    DefaultParser[key] = comp;
}

DefaultParser.YYSearchCondition = YYSearchCondition;
DefaultParser.YYSearchItem = YYSearchItem;
DefaultParser.YYStepsNav = YYStepsNav;
DefaultParser.YYStepsNavItem = YYStepsNavItem;
DefaultParser.YYEditor = YYEditor;
DefaultParser.YYEnumSelect = YYEnumSelect;

module.exports = DefaultParser;