var React = require('react');
var YYClass = require('../base/YYClass');
var PropTypes = YYClass.PropTypes;
var field = YYClass.field;

var _require = require('antd');

var Spin = _require.Spin;
//定义META

var uiMeta = {
    name: 'spin',
    fields: [field('size', 'default', PropTypes.oneOf(['default', 'small', 'large']), false, 'spin组件中点的大小'), field('spinning', true, PropTypes.bool, false, '用于内嵌其他组件的模式，可以关闭 loading 效果'), field('tip', null, PropTypes.string, false, '自定义描述文案')]
};
var YYSpin = YYClass.create({
    uiMeta: uiMeta,
    render: function render() {
        var cls = this.className();
        return React.createElement(
            'div',
            { className: cls },
            React.createElement(
                Spin,
                this.props,
                this.props.children
            )
        );
    }
});
module.exports = YYSpin;